import React from 'react';
import './App.css';

import YouTubeVideoListHolder from '../YouTubeVideoListHolder/YouTubeVideoListHolder';
import Header from '../Header/Header';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      howard_billy_clip:[],
      cousin_clip:[],
      howard_clip:[],
      full_clip:[],
      lkc_clip:[],
      wedding_clip:[],
      categoryNames:['howard_billy_clip', 'cousin_clip', 'howard_clip',
      'full_clip', 'lkc_clip', 'wedding_clip']
    };
  }


  callAPI(videoCategory, categoryArray) {
    fetch(videoCategory)
      .then(res => res.json())
      .then(res => this.setState({categoryArray: res}))
      .catch(err => err);
  }

  componentWillMount() {
    let url = "http://localhost:9000/videos/";
    this.callAPI(url+"'howard_billy_clip'", "howard_billy_clip");
    this.callAPI(url+"'cousin_clip'", "cousin_clip");
    this.callAPI(url+"'howard_clip'", "howard_clip");
    this.callAPI(url+"'lkc_clip'", "lkc_clip");
    this.callAPI(url+"'full_clip'", "full_clip");
    this.callAPI(url+"'wedding_clip'", "wedding_clip");
  }

  render() {
    return (
      <div className="App">
        <Header />
        <YouTubeVideoListHolder videoList={this.state} categoryNames={this.state.categoryNames}  />
      </div>
    );
  }
}

export default App;
