import React from 'react';
import './App.css';

import YouTubeVideoListHolder from '../YouTubeVideoListHolder/YouTubeVideoListHolder';
import Header from '../Header/Header';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      Howard_and_Billy_Videos:[],
      G4_Cousins_Family_Videos:[],
      Howard_Videos:[],
      Full_Family_Videos:[],
      LKC_Videos:[],
      Wedding_Videos:[],
      Random_Video:[],

      categoryNames:['Howard_and_Billy_Videos', 'G4_Cousins_Family_Videos', 'Howard_Videos',
      'Full_Family_Videos', 'LKC_Videos', 'Wedding_Videos']
    };
  }


  callAPI(videoCategory, categoryArray) {
    fetch(videoCategory)
      .then(res => res.json())
      .then(res => this.setState({[categoryArray]: res}))
      .catch(err => err);

  }

  componentWillMount() {
    let url = "http://localhost:9000/videos/";
    this.state.categoryNames.map(videoCategory => {
      this.callAPI(url+`"${videoCategory}"`, videoCategory);
    });
    this.randomVideo();
  }

  randomVideo(){
    var array = this.state.Howard_and_Billy_Videos;
    var index = Math.floor(Math.random()*array.length);
    this.setState({Random_Video:array[index]});
  }

  render() {
    return (
      <div className="App">
        <Header randomVideo={this.state.Random_Video}/>
        <YouTubeVideoListHolder videoList={this.state} categoryNames={this.state.categoryNames} />
      </div>
    );
  }
}

export default App;
