import React, {createRef} from 'react';
import './YouTubeVideoListHolder.css';

import YouTubeVideoList from '../YouTubeVideoList/YouTubeVideoList';

import {Nav} from 'react-bootstrap';


class YouTubeVideoListHolder extends React.Component {
  scrollDiv = createRef();  

  scrollSmoothHandler = () => {
    this.scrollDiv.current.scrollIntoView({behavior: "smooth"});
  };

  addSpace(title){
    return title.split('_').join(' ');
  }

  render() {
    let counter = 0;
    let link = "link-";
    return (
      <div className="video_list_holder">
        <Nav fill variant="pills" className="scrollbar">
          {
              this.props.categoryNames.map(videoCategory => {
                counter++;
                return <Nav.Item onClick={this.scrollSmoothHandler} className="scrollbar-item"><Nav.Link className="scrollbar-item-link" eventKey={link+counter}>{this.addSpace(videoCategory)}</Nav.Link></Nav.Item>
              })
            }
        </Nav>
        {
          this.props.categoryNames.map(videoCategory => {
            return <YouTubeVideoList ref={this.scrollDiv} videoList={this.props.videoList[videoCategory]} categoryTitle={videoCategory} id={videoCategory}/>
          })
        }
      <p ref={this.scrollDiv}>HELLO</p>
      </div>
    );
  }
}

export default YouTubeVideoListHolder;
