import React from 'react';
import './App.css';

import YouTubeVideoListHolder from '../YouTubeVideoListHolder/YouTubeVideoListHolder';
import Header from '../Header/Header';
import VideoModal from '../VideoModal';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      Howard_and_Billy_Videos:[],
      G4_Cousins_Family_Videos:[],
      Howard_Videos:[],
      Full_Family_Videos:[],
      LKC_Videos:[],
      Wedding_Videos:[],

      categoryNames:['Howard_and_Billy_Videos', 'G4_Cousins_Family_Videos', 'Howard_Videos',
      'Full_Family_Videos', 'LKC_Videos', 'Wedding_Videos'],

      addModalShow: false
    };
  }


  callAPI(videoCategory, categoryArray) {
    fetch(videoCategory)
      .then(res => res.json())
      .then(res => this.setState({[categoryArray]: res}))
      .catch(err => err);
  }

  componentWillMount() {
    let url = "http://localhost:9000/videos/";
    this.state.categoryNames.map(videoCategory => {
      this.callAPI(url+`"${videoCategory}"`, videoCategory);
    });
  }

  render() {
    return (
      <div className="App">
        <VideoModal show={this.state.addModalShow} onHide={this.setState({addModalShow: false})} link={this.props.link} id={this.props.id} title={this.props.title} />
        <Header />
        <YouTubeVideoListHolder videoList={this.state} categoryNames={this.state.categoryNames} modal={this.state.addModalShow}/>
      </div>
    );
  }
}

export default App;
