import React from 'react';
import './Header.css';

import {Nav} from 'react-bootstrap';

class Header extends React.Component {
  addSpace(title){
    return title.split('_').join(' ');
  }
  render() {
  	let counter = 0;
  	let link = "link-";
    return (
    	<div className="header">
	    	<h1 className="header_title">The Liu Family's Home Videos</h1>
	    	<p className="header_description">A collection of the Liu family's home videos throughout the years</p> 
	    	<Nav fill variant="pills" className="scrollbar">
		    	{
		          this.props.categoryNames.map(videoCategory => {
		          	counter++;
		            return <Nav.Item className="scrollbar-item"><Nav.Link active className="scrollbar-item-link" eventKey={link+counter}>{this.addSpace(videoCategory)}</Nav.Link></Nav.Item>
		          })
		        }
			</Nav>
    	</div>
    );
  }
}

export default Header;
